import React, { Component }   from 'react';
import Square from './Components/Square';
import CalcWinner from './Components/CalcWinner';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      //method to set up empty array
      squares: Array(9).fill(null),
      isNextX: true
    };
    this.handleClick = this.handleClick.bind(this);
    this.handleStart = this.handleStart.bind(this);
  }

  handleClick(e) {
    const index = e.target.value;
    const squares = this.state.squares.slice();
    if(CalcWinner(squares)) { return null };
    squares[index] = this.state.isNextX ? 'X': 'O';
    this.setState({
      squares: squares,
      //change state to true to false
      isNextX: !this.state.isNextX
    });    
  }

  handleStart() {
    this.setState({
      //reset the state
      squares: Array(9).fill(null),
      isNextX: true 
    })
  }

  render() {
    const { squares, isNextX } = this.state;
    //use like js function here, why? this is not for rendering
    const win = CalcWinner(squares);
    let status;
    if(win) {
      status = <h5>The winner is {win}</h5>
    } else {
      status = <h5>The next move is {isNextX ? 'X': 'O'}</h5>
    }
    //mark={squares[i]} pass the state to the square
    //pass the value to the square
    return (
       <div>
        {status}
        <ul className='board'>
          <Square 
            onClick={this.handleClick} 
            value='0'
            mark={squares[0]}
          />
          <Square 
            onClick={this.handleClick} 
            value='1'
            mark={squares[1]}
          />
          <Square 
            onClick={this.handleClick} 
            value='2'
            mark={squares[2]}
          />
          <Square 
            onClick={this.handleClick} 
            value='3'
            mark={squares[3]}
          />
          <Square 
            onClick={this.handleClick} 
            value='4'
            mark={squares[4]}
          />
          <Square 
            onClick={this.handleClick} 
            value='5'
            mark={squares[5]}
          />
          <Square 
            onClick={this.handleClick} 
            value='6'
            mark={squares[6]}
          />
          <Square 
            onClick={this.handleClick} 
            value='7'
            mark={squares[7]}
          />
          <Square 
            onClick={this.handleClick} 
            value='8'
            mark={squares[8]}
          />        
        </ul>
        <button onClick={this.handleStart} >Start Again</button>
      </div>  
    );  
  }

}

export default App;
