import React, { Component }   from 'react';
import Square from './Components/Square';
import CalcWinner from './Components/CalcWinner';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      //method to set up empty array
      //state is an object, its items can be array
      history: [
        [
          null, null, null,
          null, null, null,
          null, null, null
        ]
      ],  
      isNextX: true
    };
    this.handleClick = this.handleClick.bind(this);
    this.handleStart = this.handleStart.bind(this);
  }

  handleClick(e) {
    const index = e.target.value; 
    const history = this.state.history
    const current = history.squares[history.length-1].slice(); 
    if(CalcWinner(current)) { return null };
    current[index] = this.state.isNextX ? 'X': 'O';
    this.setState({
      history: history.push(current[index]),
      //change state to true to false
      isNextX: !this.state.isNextX
    });    
  }

  handleStart() {
    this.setState({
      //reset the state
      history: [ { squares: Array(9).fill(null) } ],  
      isNextX: true
    })
  }

  render() {
    const { history, isNextX } = this.state;
    //use like js function here, why? this is not for rendering
    const current = history[history.length-1];
    const win = CalcWinner(current);
    let status;
    if(win) {
      status = <h5>The winner is {win}</h5>
    } else {
      status = <h5>The next move is {isNextX ? 'X': 'O'}</h5>
    }
    //mark={squares[i]} pass the state to the square
    //pass the value to the square
    return (
       <div>
        {status}
        <ul className='board'>
          <Square 
            onClick={this.handleClick} 
            value='0'
            mark={current[0]}
          />
          <Square 
            onClick={this.handleClick} 
            value='1'
            mark={current[1]}
          />
          <Square 
            onClick={this.handleClick} 
            value='2'
            mark={current[2]}
          />
          <Square 
            onClick={this.handleClick} 
            value='3'
            mark={current[3]}
          />
          <Square 
            onClick={this.handleClick} 
            value='4'
            mark={current[4]}
          />
          <Square 
            onClick={this.handleClick} 
            value='5'
            mark={current[5]}
          />
          <Square 
            onClick={this.handleClick} 
            value='6'
            mark={current[6]}
          />
          <Square 
            onClick={this.handleClick} 
            value='7'
            mark={current[7]}
          />
          <Square 
            onClick={this.handleClick} 
            value='8'
            mark={current[8]}
          />        
        </ul>
        <button onClick={this.handleStart} >Start Again</button>
      </div>  
    );  
  }

}

export default App;
